styleContent:
.winHTML{
    background:none;
}
.darkMode .winHTML{
    background:none;
}
#FIL2cntn, #FIL2sidebar{
    background:none !important;
}
#FIL2tbl div{
    background:#FFF;
}
.darkMode #FIL2tbl div{
    background:#000;
}
#FIL2search{
    border-bottom-left-radius:5px;
    border-bottom-right-radius:5px;
}
#win_notepad2_html div{
    background:none !important;
}
#win_notepad2_html div div{
    background:#FFF !important;
}
.darkMode #win_notepad2_html div div{
    background:#000 !important;
}
#stickyNotePad{
    background:none !important;
    box-shadow:none;
}
.darkMode #stickyNotePad{
    color:#FFF;
}
